// https://leetcode-cn.com/problems/word-search/submissions/
var exist = function (board: string[][], word: string) {
    let traversed: {
        [key: string]: boolean
    } = {};
    const getKey = (i: number, j: number) => `${i}:${j}`;
    const remainSeach = word.split('');
    if (remainSeach.length === 0) {
        return false;
    }
    const search = (len: number, [startI, startJ]: [number, number]): boolean => {
        function oneDirectSearch(i: number, j: number): boolean {
            if (len > 0) {
                let target: string = remainSeach[remainSeach.length - len];
                if (!board[i]) {
                    return false;
                }
                let current = board[i][j];
                if (current === target && !traversed[getKey(i, j)]) {
                    // 标记该位置不能碰
                    traversed[getKey(i, j)] = true;
                    // 递归
                    let res = search(len - 1, [i, j]);
                    if (!res) {
                        traversed[getKey(i, j)] = false;
                    }
                    return res;

                } else {
                    // 此路不通
                    return false;
                }
            } else {
                return true;
            }
        }
        // 四个方向搜索
        return oneDirectSearch(startI, startJ + 1) || oneDirectSearch(startI - 1, startJ) || oneDirectSearch(startI + 1, startJ) || oneDirectSearch(startI, startJ - 1);
    }
    let currentSearch = remainSeach.shift();
    for (let i = 0; i < board.length; i++) {
        for (let j = 0; j < board[i].length; j++) {
            let current = board[i][j];
            if (current === currentSearch) {
                if (remainSeach.length === 0) {
                    return true;
                } else {
                    // 标记
                    traversed[getKey(i, j)] = true;
                    // 往某个方向前进；
                    if (!search(remainSeach.length, [i, j])) {
                        traversed = {};
                    } else {
                        return true;
                    }
                }
            }
        }
    }
    return false;
};


